import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Button } from 'react-materialize';
import Dante from 'Dante2';
import { ImageBlockConfig } from 'Dante2/package/es/components/blocks/image';
import ImageUploader from '../../../utils/ImageUploader';
import { saveArticle } from '../../../actions/createArticleActions';


class CreateArticle extends Component {
  constructor(props) {
    super(props);
    this.state = {
        article: {
            title: '',
            body: '',
            description: '',
            slug: '',
        },
        errors: {}
    };
  }

  publishArticle = () => {
    const { article } = this.state.article;
    const { saveArticle } = this.props;
    if (!readOnly) {
      this.createArticle();
      this.setState({ article: Object.assign(article, { published: true }) });
      if (!article.image && this.getImages().length > 0) {
        this.setState({ article: Object.assign(article, { image: this.getImages()[0] }) });
      }
      saveArticle({ ...article, published: true }, (newArticle) => {
        history.push(`/article/@${newArticle.author.username}/${newArticle.slug}`);
      });
    }
  };

  handleUpload = e => {
    const selectFile = e.target.files[0];
    ImageUploader({ image: selectFile }).then((res) =>{
        //console.log(res)
      this.setState({
        image: res.data.secure_url
      })
    }
    );
  };
  render() {
      console.log(this.props)
    return (
      <div >
        <Dante

            data_storage={{
                interval: 500,
                url: ImageUploader.path,
                method: 'POST',
                save_handler: this.handleSave,
            }}
            widgets={[
                ImageBlockConfig({
                upload_callback: this.handleUpload,
                upload_handler: this.handleUpload
                })
          ]}
        />
        <Button onClick={ this.props}>PUBLISH</Button>
      </div>
    );
  }
}

const mapStateToProps = state => ({
    article: state.article,
    errors: state.errors
  });

  export default withRouter(
    connect(
      mapStateToProps,
      { saveArticle }
    )(CreateArticle)
  );
