import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
// import PropTypes from 'prop-types';
import { Button } from 'react-materialize';
import Dante from 'Dante2';
//import { PlaceholderBlockConfig } from 'Dante2/package/es/components/blocks/placeholder';
import { saveArticle } from '../../../actions/createArticleActions';



class CreateArticle extends Component {
  constructor(props) {
    super(props);
    this.state = {
        article: {
          title: '', body: '', description: '', tags: [],
        },
        errors: {},
        display : [],
        error : ''};
        this.handleChange = this.handleChange.bind(this);
  }




  render() {
    //const name = this.props.name;
    return (

      <div >
         <input type='text' placeholder='Title'/>
         <input type='text' placeholder='Description'/>
        <Dante
            onChange={editor => { console.log('editor content: ', editor.emitSerializedOutput()) }}

        />

        <Button onClick={ this.publishArticle}>PUBLISH</Button>
      </div>
    );
  }
}

// CreateArticle.propTypes = {
//     saveArticle: PropTypes.func.isRequired,
//     //errors: PropTypes.object.isRequired,
//     //editorState: PropTypes.shape({}),
//   };
// CreateArticle.defaultProps = {
//     editorState: PropTypes.shape({}),
// }
const mapStateToProps = state => ({
    article: state.article,
    errors: state.errors
  });

  export default withRouter(
    connect(
      mapStateToProps,
      { saveArticle }
    )(CreateArticle)
  );
