import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import TagsInput from 'react-tagsinput';
import 'react-tagsinput/react-tagsinput.css'
import { ImageBlockConfig } from 'Dante2/package/es/components/blocks/image';
import { Button } from 'react-materialize';
import Dante from 'Dante2';
import { saveArticle } from '../../../actions/createArticleActions';

import upload from '../../../utils/ImageUploader';



class CreateArticle extends Component {
    constructor() {
        super();
        this.state = {
        title: '',
        description: '',
        body: '',
        tags: [],
        image: '',
        }
    }   

    getEditorContent = () => this.editor.emitSerializedOutput();

    getImages = () => {
        const images = [];
        if (this.editor) {
          const { blocks } = this.getEditorContent();

          for (let i = 0; i < blocks.length; i += 1) {
            let image = null;
            if (blocks[i].type === 'image') {
              const { data: { url } } = blocks[i];
              image = url;
            }
            if (image) {
              images.push(image);
            }
          }
        }
        return images;
      };

    createArticle = () => {
        const state = this.editor;
        if (state != null) {
          const editorState = this.getEditorContent();
          const { article, image } = this.state;

          for (let i = 0; i < editorState.blocks.length; i += 1) {
            const block = editorState.blocks[i];
            if (!title && block.type.startsWith("header")) {
              title = block.text;
            }
            if (!description && block.type === 'unstyled') {
              description = block.text.substring(0, 128);
              break;
            }
          }
          if (!title) {
            title = description;
          }
          if (!description) {
            description = title;
          }
          if (chosenImage) {
            this.setState({ article: { ...article, image: chosenImage } });
          }
          this.setState({
            article: Object.assign(article, {
              title,
              description,
              body: JSON.stringify(editorState),
            }),
          });
        }
      };

    handleTagChange(tags) {
        this.setState({tags})
    }
    titleDescriptionChange = e => {
        this.setState({ [e.target.id]: e.target.value });
      };
    handleUpload = (file, state) => {
        upload({
          body: file,
        })
          .then((data) => {
            state.uploadCompleted(data.secure_url);
          });
      };


  render() {
    return (

      <div >
        <input
            id= 'title'
            onChange={this.titleDescriptionChange}
            type='text' 
            placeholder='Title'/>
        <input
            id= 'description'
            onChange={this.titleDescriptionChange}
            type='text' placeholder='Description'/>
        <Dante
            content={null}
            widgets={[ImageBlockConfig({
                options: {
                    upload_url: 'https://api.cloudinary.com/v1_1/dadrqjrpw/image/upload',
                    upload_callback: this.handleUpload,
                    upload_handler: this.handleUpload,
                }
            })]}
        />

        <TagsInput value={this.state.tags} onChange={ this.handleTagChange.bind(this)} />
        <Button onClick={ this.publishArticle}>PUBLISH</Button>
      </div>
    );
  }
}

// CreateArticle.propTypes = {
//     saveArticle: PropTypes.func.isRequired,
//     //errors: PropTypes.object.isRequired,
//     //editorState: PropTypes.shape({}),
//   };
// CreateArticle.defaultProps = {
//     editorState: PropTypes.shape({}),
// }
const mapStateToProps = state => ({
    article: state.article,
    errors: state.errors
  });

  export default withRouter(
    connect(
      mapStateToProps,
      { saveArticle }
    )(CreateArticle)
  );
